  def test_setup_yaml yaml_repo
    yaml_repo.instance_variable_set(:@config_dir, File.expand_path("../", __FILE__))
    yaml_repo.instance_variable_set(:@config_files, ["config"])
    yaml_repo.send(:build!)
    yaml_repo.send(:load_yaml_default)
  end
  
  def load_hash id, type
    extdir = File.expand_path("../../battlefield", __FILE__)
    @local_repo.load "#{extdir}/#{type}_#{id}.json"
  end
  
  def publish data, filename
    extfile = File.expand_path("../../battlefield/", __FILE__) + "/" + filename.split("/").last
    @local_repo.save data.to_hash, extfile
  end
